components.calculateValue:
if (!data.endDate) {
  return 'n/a'
}

var remaining = moment(data.endDate).diff(moment(), 'days', true)

if (remaining < 0) {
  return 'EXPIRED'
}

var str =
  Math.floor(remaining) + 'd ' +
  moment().startOf('day')
    .add(remaining % 1 * 24, 'hours')
    .format('H[h] m[m]')

value = str

components.custom:
var preset = {
  plan: 'basic',
  projects: 1,
  tenants: 0,
  stages: 0,
  emails: 10,
  forms: 10,
  formRequests: 1000,
  formManagers: 0,
  submissionRequests: 1000,
  pdfs: 1,
  pdfDownloads: 10,
  pdfServers: 0,
  apiServers: 0,
}

try {
  var instances = form.flattenComponents()

  _.each(preset, function(value, key) {
    if (!instances[key]) {
      throw new Error('Invalid key: ' + key)
    }

    if (value === null) {
      instances[key].dataValue = null
      instances[key].setValueAt(0, null)
      instances[key].resetValue()
    }

    instances[key].setValue(value)
  })

  instances.scope.setValue([
    'project',
    'form',
    'formRequest',
    'email',
    'pdf',
    'pdfDownload',
    'submissionRequest',
  ])
}
catch (e) {
  debugger
  alert('Error applying preset: ' + e.toString())
}

